mapper.select:
SELECT nn.notice_code, nn.notice_title, nn.notice_file, nn.notice_count,
tt.total_count,
nn.notice_date, nn.notice_update FROM notice_notice nn LEFT OUTER JOIN (select COUNT(*) AS total_count from notice_notice) tt ON(1 = 1) ORDER by notice_code DESC limit #{totalPage}, #{count};

mapper.update:
UPDATE
	notice_notice
SET
	notice_title = #{notice_title}
WHERE
	notice_code = #{notice_code}
